import React, { Component } from "react";
import styled from "styled-components";
import { batchLayout, motion } from "framer-motion";
import planet1 from "../images/planet1.png";
import planet2 from "../images/planet2.png";
import planet3 from "../images/planet3.jpg";

const Section = styled.section`
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background: #131313;
`;
const Container = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  heght: 100vh;
  padding: 3rem cals((100vw-1300px)/2);
`;

const ColumnLeft = styled.div`
  display: flex;
  color: #fff;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
  padding: 5rem 2rem;
  h1 {
    margin-bottom: 0.5rem;
    font-size: 2rem;
  }
  p {
    margin: 2rem 0;
    fot-size: 4rem;
    line-height: 1.1;
  }
`;

const Button = styled.div`
padding:1rem 3rem;
font-size:1rem;
border:2px solid #fff;
border-radius:4px;
outline:none;
cursor:pointer
background:transparent;
`;
const Image = styled.img`
  position: absolute;
  width: 100%;
  height: 100%;
  max-width: 250px;
  max-height: 250px;
`;

class Hero extends Component {
  render() {
    return (
      <Section>
        <Container>
          <ColumnLeft>
            <h1>Welcome To Adiya Barve WebPage</h1>
          </ColumnLeft>
          <ColumnRight>
            <Image src={planet1} alt="planet" />
            s <Image src={planet2} alt="planet" />
            <Image src={planet3} alt="planet" />
          </ColumnRight>
        </Container>
      </Section>
    );
  }
}

export default Hero;
