import React, { Component } from "react";
import styled from "styled-components";
import { batchLayout, motion } from "framer-motion";
import planet1 from "../images/planet1.png";
import planet2 from "../images/planet2.png";
import planet3 from "../images/planet3.jpg";

const Section = styled.section`
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background: #131313;
`;
const Container = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  heght: 100vh;
  padding: 3rem cals((100vw-1300px)/2);
`;

const Image = styled(motion.img)`
  position: absolute;
  width: 100%;
  height: 100%;
  max-width: 250px;
  max-height: 250px;
`;
const ColumnLeft = styled.div`
  display: flex;
  color: #fff;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
  padding: 5rem 2rem;
  h1 {
    margin-bottom: 0.5rem;
    font-size: 2rem;
  }
  p {
    margin: 2rem 0;
    font-size: 4rem;
    line-height: 1.1;
  }
`;

const ColumnRight = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
  ${Image}:nth-child(1) {
    top: 10px;
    left: 10px;
  }
  ${Image}:nth-child(2) {
    top: 170px;
    left: 10px;
  }
  ${Image}:nth-child(3) {
    top: 350px;
    left: 50px;
  }
`;

const Button = styled.div`
padding:1rem 3rem;
font-size:1rem;
border:2px solid #fff;
border-radius:4px;
outline:none;
cursor:pointer
background:transparent;
`;

class Hero extends Component {
  render() {
    const fadeleft = {
      hidden: { opacity: 0, x: -100 },
      visible: { opacity: 1, x: 0 },
    };
    return (
      <Section>
        <Container>
          <ColumnLeft>
            <motion.h1
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 2 }}
            >
              Welcome To Adiya Barve WebPage
            </motion.h1>
            <motion.p
              variants={fadeleft}
              initial="hidden"
              animate="visible"
              transition={{ duration: 1 }}
              whileHover={{ scale: 0.9 }}
            >
              Journey begins Here With Aditya!!
            </motion.p>
            <button>Read More</button>
          </ColumnLeft>
          <ColumnRight>
            <Image
              src={planet1}
              alt="planet"
              whileHover={{ scale: 0.9 }}
              drag={true}
              dragConstraints={{ left: 0, right: 250, top: 0, bottom: 50 }}
            />
          </ColumnRight>
        </Container>
      </Section>
    );
  }
}

export default Hero;
